
$(document).ready(function(){$('#server_type').on('change',function(){$('#mail, #sendmail, #smtp').css('display','none');switch($(this).val().toLowerCase()){case'mail':$('#mail').css('display','block');break;case'sendmail':$('#sendmail').css('display','block');break;case'smtp':$('#smtp').css('display','block');break;}});$('#test-form[type=submit]').click(function(e){e.preventDefault();var form=this.form,data,url;data=$(form).serialize();if(this.name){data[this.name]=this.value;}
url=$(form).attr('action');$.post(url,data,function success(data){$('#test-ajax').html(data);});});$('#server_type').trigger('change');});