<?php echo '
<script type="text/javascript">
$().ready(function(){

	var box = $(\'#box textarea\');
	var orig = box.val();

	box.focus(function() {
		if ($(this).val() == orig)
			$(this).val("");
	});

	box.blur(function() {
		var val = $(this).val();
		val.replace(/^\\s*|\\s*$/g,"");
		if (val == "")
			$(this).val(orig);
	});
	
	$(\'#box a\').click(function() { 
		$(\'#box\').hide().find(\'textarea\').val(orig);
		$(\'#file\').show();
		return false;
	});
	
	$(\'#file a\').click(function() { 
		$(\'#file\').hide().find(\'input\').val("");
		$(\'#box\').show();
		return false;
	});
	
	$(\'#type\').change(function() {
		if($(this).val() != \'csv\')
			return;
		$(\'#box\').hide().find(\'textarea\').val(orig);
		$(\'#file\').show();
	});

});
</script>
'; ?>


